AND BOARD_REPLY_TO = #{boardReplyTo}
AND MEMBER_ID = #{memberId}
AND TO_CHAR(BOARD_DATE,'YY-MM-DD HH:MI:SS') = #{boardDate}

mapper.update:

UPDATE BOARD
SET BOARD_ID= BOARD_ID-1
WHERE BOARD_ROLE = #{boardRole}
AND BOARD_ID > #{boardId}



UPDATE BOARD
SET BOARD_HIT = BOARD_HIT + 1
WHERE BOARD_ROLE= #{boardRole}
AND BOARD_ID = #{boardId}
