mapper.select:
SELECT PRODUCT_NAME, PRODUCT_PRICE, CART_QUANTITY
FROM CART
WHERE MEMBER_ID = #{memberId}

SELECT decode(count(*), 0) 'false', 'true') FROM CART
		 WHERE PRODUCT_ID = #{product_id}
		 AND member_id = #{member_id}

mapper.update:
UPDATE CART
SET CART_Quantity = #{cartQuantity}
WHERE
CART_ID = #{cartId}
