mapper.select:
SELECT *
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

	AND MEMBER_PASSWORD = #{memberPassword}

mapper.update:
UPDATE MEMBER
MEMBER_PASSWORD = #{memberPassword}, MEMBER_NAME = #{memberName}, MEMBER_TEL = #{memberTel}, MEMBER_ADDRESS = #{memberAddress}, MEMBER_GENDER = #{memberGender}, MEMBER_EMAIL = #{memberEmail}
WHERE MEMBER_ID = #{memberId}

mapper.select:
SELECT CASE WHEN COUNT(MEMBER_ID) > 0 THEN 0 ELSE 1 end AS ID
FROM MEMBER
WHERE MEMBER_ID = #{id}

SELECT MEMBER_POINT
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

mapper.update:
UPDATE MEMBER
SET MEMBER_POINT = #{memberPoint}
WHERE MEMBER_ID = #{memberId}
